Delete:
if (O_REG) begin : o_reg
  always @(posedge clk) begin
    if (reset) begin
      ovalid <= 1'b0;
    end else begin
      ovalid <= pack_wr;
  end
  always @(posedge clk) begin
    odata <= idata_packed;
  end
end else begin
  always @(*) begin
    ovalid = pack_wr;
    odata = idata_packed;
  end
end

Add:
generate
  if (O_REG) begin : o_reg
    always @(posedge clk) begin
      if (reset) begin
        ovalid <= 1'b0;
      end else begin
        ovalid <= pack_wr;
      end
    always @(posedge clk) begin
      odata <= idata_packed;
    end
  end else begin
    always @(*) begin
      ovalid = pack_wr;
      odata = idata_packed;
    end
