Delete:
always @(posedge clk) begin
  if (reset == 1'b1) begin
    lmfc_clk_p1 <= 1'b0;
  end else if (lmfc_active == 1'b1) begin
    if (lmfc_counter == cfg_whole_beats_per_multiframe) begin
      lmfc_clk_p1 <= 1'b1;
    end else if (lmfc_counter == cfg_whole_beats_per_multiframe[7:1]) begin
  end
  lmfc_clk <= lmfc_clk_p1;
end

Add:
always @(posedge clk) begin
  if (reset == 1'b1) begin
  end else if (lmfc_active == 1'b1) begin
    if (lmfc_counter == cfg_whole_beats_per_multiframe) begin
      lmfc_clk_p1 <= 1'b1;
    end else if (lmfc_counter == cfg_whole_beats_per_multiframe[7:1]) begin
      lmfc_clk_p1 <= 1'b0;
    end
  lmfc_clk <= lmfc_clk_p1;
end
