  input clk,
  input [WIDTH-1:0] in,
  output [WIDTH-1:0] out
);

generate if (REGISTERED == 0) begin

  assign out = in;

end else begin

  (* shreg_extract = "no" *)  reg [REGISTERED*WIDTH-1:0] in_dly = {REGISTERED*WIDTH{1'b0}};

  always @(posedge clk) in_dly <= {in_dly,in};

  assign out = in_dly[REGISTERED*WIDTH-1 -: WIDTH];

end endgenerate

endmodule

