Delete:
always @(posedge clk) begin
  if (reset | valid_header) begin
    header_icnt <= 'b0;
  end else if (state[BIT_SH_LOCK] & ~header_icnt[LOG2_RX_THRESH_SH_ERR]) begin
    header_icnt <= header_icnt + 'b1;
end

