// create a ff for holding the issue_stall signal
reg freez;
wire set_freez, clr_freez;
always @ (posedge clk, posedge rst) begin
  if(rst) begin
    freez <= 0;
  end
  else if(set_freez) begin
    freez <= 1;
  end
  else if(clr_freez) begin
    freez <= 0;
  end
end
assign issue_ready = ~freez;


