//Simple stall state machine
//Gets set for one cycle whenever there is no burst
//gets cleared by ack from io logic

always @ (posedge clk or negedge nreset)
  if(!nreset)
    tx_io_wait <= 1'b0;
  else if (tx_io_ack)
    tx_io_wait <= 1'b0;
  else if (tx_access & ~tx_burst)
    tx_io_wait <= 1'b1;

