Clone Blocks:
always @ (posedge rd_clk)
  read_hi <= mi_rd & (mi_addr[RFAW+1:2]==`E_MAILBOXHI);

assign mi_dout[31:0]  = read_hi ? mailbox_data[63:32] : mailbox_data[31:0];
assign mi_dout[63:32] = mailbox_data[63:32];

