//#write side state machine
//###########################

always @ ( posedge wr_clk or negedge nreset) 
  if(!nreset) 
    wr_addr[AW:0]  <= 'b0;
  else if(wr_en) 
    wr_addr[AW:0]  <= wr_addr[AW:0]  + 'd1;

//address for prog_full indicator
always @ (posedge wr_clk or negedge nreset)
  if(!nreset)
    wr_addr_ahead[AW:0] <= 'b0;
