always @ (posedge io_clk)
  if(transfer_active & dmode8)//8 bit
    shiftreg[63:0] <= {8'b0,shiftreg[IOW-8-1:0]};   
  else if(transfer_active & dmode16)//16 bit
    shiftreg[63:0] <= {16'b0,shiftreg[IOW-16-1:0]};
