Delete:
for (i=0;i<WP;i=i+1)
  always @ (posedge clk)
    if (wr_valid[i])
      mem[wr_addr[(i+1)*RAW-1:i*RAW]] <= wr_data[(i+1)*RW-1:i*RW];

Add:
   for(i=0;i<REGS;i=i+1)
     for(j=0;j<WP;j=j+1)
       assign write_en[i][j] = wr_valid[j] & (wr_addr[j*RAW+:RAW] == i);
   for(i=0;i<REGS;i=i+1)
     for(j=0;j<WP;j=j+1)
       always @ (posedge clk)
	 if (write_en[i][j])
	   mem[i] <= wr_data[j*RW+:RW];
