   always @ (posedge tx_lclk_div4)
	tx_wr_wait <= tx_wr_wait_sync;
	tx_rd_wait <= tx_rd_wait_sync;

Add:
   always @ (negedge tx_lclk_div4)
	tx_wr_wait      <= tx_wr_wait_sync;
	tx_rd_wait      <= tx_rd_wait_sync;
