//detect selection based on valid pattern edge
assign data_select[7:0] = io_valid_reg[7:0] ^ {io_valid_reg[7:1],1'b1};

integer 	      i;   
always @ (posedge rx_clk)
  for (i=0;i<8;i=i+1)
    shiftreg[i*8+:8] <= data_select[i] ? mux_data[i*8+:8] : shiftreg[i*8+:8];

