   reg [AW-1:0]          waddr;
   reg [AW-1:0]          raddr;
   reg [AW-1:0]          count;

   always @ ( posedge clk ) begin
      if( reset ) 
	begin	   
           waddr    <= 1'b0;
           raddr    <= 1'b0;
           count    <= 1'b0;
           rd_empty <= 1'b1;
           wr_full  <= 1'b0;         
      end else 
	begin
           if( wr_en & rd_en ) 
	     begin
		waddr <= waddr + 'd1;
		raddr <= raddr + 'd1;	      
             end 
	   else if( wr_en ) 
	     begin
		waddr <= waddr + 'd1;
		count <= count + 'd1;
		rd_empty <= 1'b0;
		if( & count )
		  wr_full <= 1'b1;		
         end 
	   else if( rd_en ) 
	   begin	      
              raddr <= raddr + 'd1;
              count <= count - 'd1;
              wr_full <= 1'b0;
              if( count == 'd1 )
		rd_empty <= 1'b1;	      
           end
	end // else: !if( reset )
   end // always @ ( posedge clk )

