reg [DW-1:0]     q1_sl;
reg [DW-1:0]     q2_sh;
reg [DW-1:0]     q2_sl;

// sample on rising edge
always @ (posedge clk)
  if(ce)
    q1_sl[DW-1:0] <= #0.2  din[DW-1:0];

// sampling on falling edge
always @ (negedge clk)
  if(ce)
    q2_sh[DW-1:0] <= #0.2  din[DW-1:0];

     begin
	q1_sl[DW-1:0] <= #(0.1) din1[DW-1:0];
	q2_sl[DW-1:0] <= #(0.1) din2[DW-1:0];
     end

   always @ (negedge clk)
     q2_sh[DW-1:0] <= #(0.1) q2_sl[DW-1:0];

   assign out[DW-1:0] = clk ? q1_sl[DW-1:0] : 
	                      q2_sh[DW-1:0];

endmodule // oh_oddr

